

const X_CLASS="x",CIRCLE_CLASS="circle",WINNING_COMBINATIONS=[[0,1,2],[3,4,5],[6,7,8],[0,3,6],[1,4,7],[2,5,8],[0,4,8],[2,4,6]],cellElements=document.querySelectorAll("[data-cell]"),board=document.getElementById("board"),winningMessageElement=document.getElementById("winningMessage"),restartButton=document.getElementById("restartButton"),winningMessageTextElement=document.querySelector("[data-winning-message-text]");let circleTurn;function startGame(){circleTurn=!1,cellElements.forEach(cell=>{cell.classList.remove("x"),cell.classList.remove("circle"),cell.removeEventListener("click",handleClick),cell.addEventListener("click",handleClick,{once:!0})}),setBoardHoverClass(),winningMessageElement.classList.remove("show")}function handleClick(e){const cell=e.target,currentClass=circleTurn?"circle":"x";placeMark(cell,currentClass),checkWin(currentClass)?endGame(!1):isDraw()?endGame(!0):(swapTurns(),setBoardHoverClass())}function endGame(draw){winningMessageTextElement.innerText=draw?"Draw!":`${circleTurn?"O's":"X's"} Wins!`,winningMessageElement.classList.add("show")}function isDraw(){return[...cellElements].every(cell=>cell.classList.contains("x")||cell.classList.contains("circle"))}function placeMark(cell,currentClass){cell.classList.add(currentClass)}function swapTurns(){circleTurn=!circleTurn}function setBoardHoverClass(){board.classList.remove("x"),board.classList.remove("circle"),circleTurn?board.classList.add("circle"):board.classList.add("x")}function checkWin(currentClass){return WINNING_COMBINATIONS.some(combination=>combination.every(index=>cellElements[index].classList.contains(currentClass)))}startGame(),restartButton.addEventListener("click",startGame);var colors=["#760CE8","#4782B1","#E8890C","#EF6C00","#764ba2"],changeBackground=function(){document.body.style.background=colors[Math.floor(Math.random()*colors.length)],document.getElementById("footerLogo").style.color="#eeeeee"};changeBackground();